// Enhanced ChatGPT 5 Request Processor
const userRequest = $input.first().json;
const { query, context, userRole, urgency, complexity, preferredLLM, useChatGPT5 } = userRequest;

// Load LLM configuration from bilateral sync system
const llmConfig = {
  chatgpt5: {
    provider: 'openai',
    model: 'gpt-5',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.3,
    advancedFeatures: {
      functionCalling: true,
      vision: true,
      codeInterpreter: true,
      retrieval: true
    }
  },
  claude: {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 2000,
    temperature: 0.3
  }
};

// Determine optimal LLM based on request characteristics
let selectedLLM;
let reasoning;

if (useChatGPT5 && preferredLLM === 'chatgpt') {
  selectedLLM = 'chatgpt5';
  reasoning = 'User explicitly requested ChatGPT 5 for enhanced capabilities';
} else if (complexity === 'complex' || complexity === 'expert') {
  selectedLLM = 'chatgpt5';
  reasoning = 'Complex request requires ChatGPT 5 advanced reasoning';
} else if (context === 'technical' || context === 'engineering') {
  selectedLLM = 'chatgpt5';
  reasoning = 'Technical context benefits from ChatGPT 5 code interpretation';
} else if (urgency === 'critical' || urgency === 'high') {
  selectedLLM = 'chatgpt5';
  reasoning = 'High urgency requires ChatGPT 5 superior problem-solving';
} else {
  selectedLLM = 'claude';
  reasoning = 'Standard request suitable for Claude cost-effectiveness';
}

// Prepare enhanced request with LLM selection
const enhancedRequest = {
  ...userRequest,
  selectedLLM,
  llmConfig: llmConfig[selectedLLM],
  reasoning,
  timestamp: new Date().toISOString(),
  bilateralSync: {
    enabled: true,
    autoSync: true,
    syncPriority: selectedLLM === 'chatgpt5' ? 'high' : 'normal'
  }
};

return enhancedRequest;

