// Enhanced Crew Response Generator with Bilateral Sync Integration
const aiResponse = $input.first().json.choices[0].message.content.toLowerCase();
const originalRequest = $('Intelligent LLM Selector').first().json;

// Extract crew member recommendation
let crewMember = 'captain-picard'; // default
if (aiResponse.includes('data')) crewMember = 'lieutenant-data';
else if (aiResponse.includes('troi')) crewMember = 'counselor-troi';
else if (aiResponse.includes('scott')) crewMember = 'chief-engineer-scott';
else if (aiResponse.includes('spock')) crewMember = 'commander-spock';
else if (aiResponse.includes('worf')) crewMember = 'lieutenant-worf';
else if (aiResponse.includes('observation')) crewMember = 'observation-lounge';

// Extract UI layout recommendation
let uiLayout = 'standard-lcars'; // default
if (aiResponse.includes('technical') || aiResponse.includes('engineering')) uiLayout = 'technical-analysis';
else if (aiResponse.includes('strategic') || aiResponse.includes('command')) uiLayout = 'strategic-command';
else if (aiResponse.includes('security') || aiResponse.includes('tactical')) uiLayout = 'security-tactical';

// Extract priority level
let priority = 'normal'; // default
if (aiResponse.includes('critical') || aiResponse.includes('urgent')) priority = 'critical';
else if (aiResponse.includes('high')) priority = 'high';
else if (aiResponse.includes('low')) priority = 'low';

// Prepare enhanced crew request with bilateral sync context
const crewRequest = {
  query: originalRequest.query,
  context: originalRequest.context || 'general',
  userRole: originalRequest.userRole || 'developer',
  urgency: originalRequest.urgency || 'normal',
  complexity: originalRequest.complexity || 'medium',
  selectedCrew: crewMember,
  aiSelection: aiResponse,
  llmInfo: {
    provider: originalRequest.selectedLLM,
    model: originalRequest.llmConfig.model,
    maxTokens: originalRequest.llmConfig.maxTokens
  },
  uiRecommendation: {
    layout: uiLayout,
    priority: priority,
    reasoning: aiResponse
  },
  bilateralSync: {
    ...originalRequest.bilateralSync,
    lastSync: new Date().toISOString(),
    syncStatus: 'pending'
  }
};

return crewRequest;

// Bilateral Sync Integration and Workflow Enhancement Analyzer
const crewRequest = $input.first().json;
const { selectedCrew, uiRecommendation, bilateralSync, llmInfo } = crewRequest;

// Analyze if workflow enhancements are needed
const workflowEnhancements = [];

if (llmInfo.provider === 'chatgpt5') {
  workflowEnhancements.push({
    type: 'llm_upgrade',
    priority: 'high',
    description: 'Upgrade workflow to leverage ChatGPT 5 advanced features',
    recommendations: [
      'Enable function calling for crew selection',
      'Implement advanced error handling',
      'Add performance monitoring',
      'Enable real-time sync optimization'
    ]
  });
}

if (uiRecommendation.priority === 'critical' || uiRecommendation.priority === 'high') {
  workflowEnhancements.push({
    type: 'ui_optimization',
    priority: uiRecommendation.priority,
    description: 'Optimize UI layout for high-priority requests',
    recommendations: [
      'Implement priority-based UI rendering',
      'Add real-time status updates',
      'Enable emergency mode for critical requests',
      'Optimize response time for high-priority operations'
    ]
  });
}

if (bilateralSync.enabled) {
  workflowEnhancements.push({
    type: 'sync_optimization',
    priority: 'medium',
    description: 'Optimize bilateral sync for enhanced performance',
    recommendations: [
      'Implement smart conflict resolution',
      'Add sync performance metrics',
      'Enable predictive sync scheduling',
      'Optimize file watching efficiency'
    ]
  });
}

// Prepare enhanced response with workflow recommendations
const enhancedResponse = {
  success: true,
  selectedCrew: crewRequest.selectedCrew,
  aiSelection: crewRequest.aiSelection,
  llmInfo: crewRequest.llmInfo,
  uiRecommendation: crewRequest.uiRecommendation,
  bilateralSync: {
    ...crewRequest.bilateralSync,
    syncStatus: 'optimized',
    lastOptimization: new Date().toISOString()
  },
  workflowEnhancements,
  timestamp: new Date().toISOString(),
  systemHealth: {
    status: 'optimal',
    recommendations: workflowEnhancements.length > 0 ? 'Workflow enhancements available' : 'System operating optimally',
    nextSync: new Date(Date.now() + 60000).toISOString() // 1 minute from now
  }
};

return enhancedResponse;

