// Local Test Data Generator for n8n UI Testing
// This allows testing the workflow directly in the n8n interface

const testScenarios = [
  {
    query: "What should I work on today?",
    context: "daily-planning",
    userRole: "developer",
    urgency: "normal",
    complexity: "medium"
  },
  {
    query: "How do we optimize this React component?",
    context: "performance-optimization",
    userRole: "senior-developer",
    urgency: "high",
    complexity: "high"
  },
  {
    query: "I'm feeling overwhelmed with this project",
    context: "emotional-support",
    userRole: "junior-developer",
    urgency: "normal",
    complexity: "low"
  },
  {
    query: "We need to secure our API endpoints",
    context: "security-review",
    userRole: "security-engineer",
    urgency: "critical",
    complexity: "high"
  },
  {
    query: "The deployment pipeline is failing",
    context: "infrastructure-issue",
    userRole: "devops-engineer",
    urgency: "critical",
    complexity: "high"
  },
  {
    query: "Let's review the project status",
    context: "project-review",
    userRole: "project-manager",
    urgency: "normal",
    complexity: "medium"
  }
];

// Select a random test scenario or use the first one
const selectedScenario = testScenarios[Math.floor(Math.random() * testScenarios.length)];

// Add request ID and timestamp
selectedScenario.requestId = 'test-' + Math.random().toString(36).substr(2, 9);
selectedScenario.timestamp = new Date().toISOString();
selectedScenario.testMode = true;

return selectedScenario;

