// Response Synthesizer - Enhanced response processing
const synthesizeResponse = () => {
  const requestData = $input.all()[0].json;

  let crewResponse;
  try {
    if (typeof requestData === 'string') {
      crewResponse = { message: requestData };
    } else if (requestData.response) {
      crewResponse = requestData.response;
    } else {
      crewResponse = requestData;
    }
  } catch (error) {
    crewResponse = {
      message: 'I apologize, but I encountered an issue processing your request.',
      error: true
    };
  }

  const totalProcessingTime = Date.now() - requestData.processingStartTime;

  const synthesizedResponse = {
    response: crewResponse,
    metadata: {
      requestId: requestData.requestId,
      selectedCrew: requestData.selectedCrew,
      confidence: requestData.confidence,
      processingTime: totalProcessingTime,
      qualityScore: calculateQualityScore(crewResponse, requestData),
      completedAt: new Date().toISOString()
    }
  };

  return [{ json: synthesizedResponse }];
};

function calculateQualityScore(response, requestData) {
  let score = 0.5;
  const responseText = JSON.stringify(response);
  if (responseText.length > 100) score += 0.2;
  if (requestData.confidence > 0.8) score += 0.2;
  if (!response.error) score += 0.3;
  return Math.min(1.0, score);
}

return synthesizeResponse();

// Request Preprocessor - Enhanced request validation and enrichment
const preprocessRequest = () => {
  const input = $input.all()[0].json;

  // Extract and validate required fields
  const query = input.query || '';
  const context = input.context || 'general';
  const urgency = input.urgency || 'normal';
  const userRole = input.userRole || 'user';
  const sessionId = input.sessionId || `session_${Date.now()}`;

  // Classify query type for intelligent routing
  const queryClassification = classifyQuery(query);

  // Enrich with metadata
  const enrichedRequest = {
    query,
    context,
    urgency,
    userRole,
    sessionId,
    queryType: queryClassification.type,
    complexity: queryClassification.complexity,
    estimatedTokens: Math.ceil(query.length / 4),
    timestamp: new Date().toISOString(),
    requestId: `req_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    suggestedCrewMembers: queryClassification.suggestedCrew,
    requiresMultipleAgents: queryClassification.complexity === 'high',
    processingStartTime: Date.now()
  };

  return [{ json: enrichedRequest }];
};

// Query classification logic
function classifyQuery(query) {
  const lowerQuery = query.toLowerCase();

  if (lowerQuery.includes('technical') || lowerQuery.includes('code') || lowerQuery.includes('debug')) {
    return { type: 'technical', complexity: 'high', suggestedCrew: ['data', 'scott'] };
  }

  if (lowerQuery.includes('strategy') || lowerQuery.includes('decision') || lowerQuery.includes('leadership')) {
    return { type: 'strategic', complexity: 'high', suggestedCrew: ['picard', 'spock'] };
  }

  if (lowerQuery.includes('feel') || lowerQuery.includes('team') || lowerQuery.includes('conflict')) {
    return { type: 'interpersonal', complexity: 'medium', suggestedCrew: ['troi', 'picard'] };
  }

  if (lowerQuery.includes('security') || lowerQuery.includes('risk') || lowerQuery.includes('protect')) {
    return { type: 'security', complexity: 'medium', suggestedCrew: ['worf', 'spock'] };
  }

  return { type: 'general', complexity: 'low', suggestedCrew: ['picard', 'data'] };
}

return preprocessRequest();

