// Dynamic Crew Router - Enhanced routing with fallback
const routeToCrewMember = () => {
  const requestData = $input.all()[0].json;
  const aiResponse = requestData.choices[0].message.content;

  let crewSelection;
  try {
    crewSelection = JSON.parse(aiResponse);
  } catch (error) {
    crewSelection = {
      selectedCrew: extractCrewFromText(aiResponse),
      confidence: 0.5,
      reasoning: 'Fallback parsing used',
      alternativeCrew: ['picard'],
      requiresTeam: false
    };
  }

  const validCrewMembers = ['picard', 'data', 'troi', 'scott', 'spock', 'worf', 'observation-lounge'];
  if (!validCrewMembers.includes(crewSelection.selectedCrew)) {
    crewSelection.selectedCrew = 'picard';
    crewSelection.confidence = 0.3;
  }

  const routingData = {
    ...requestData,
    selectedCrew: crewSelection.selectedCrew,
    confidence: crewSelection.confidence,
    reasoning: crewSelection.reasoning,
    alternativeCrew: crewSelection.alternativeCrew || ['picard'],
    requiresTeam: crewSelection.requiresTeam || false,
    routingDecisionTime: Date.now() - requestData.processingStartTime,
    fallbackUsed: crewSelection.confidence < 0.7,
    crewMemberEndpoint: getCrewMemberEndpoint(crewSelection.selectedCrew)
  };

  return [{ json: routingData }];
};

function extractCrewFromText(text) {
  const crewMembers = ['picard', 'data', 'troi', 'scott', 'spock', 'worf', 'observation-lounge'];
  const lowerText = text.toLowerCase();

  for (const crew of crewMembers) {
    if (lowerText.includes(crew)) {
      return crew;
    }
  }
  return 'picard';
}

function getCrewMemberEndpoint(crewMember) {
  const endpoints = {
    'picard': 'https://alexai-star-trek-agile.vercel.app/api/crew/captain-picard',
    'data': 'https://alexai-star-trek-agile.vercel.app/api/crew/lieutenant-data',
    'troi': 'https://alexai-star-trek-agile.vercel.app/api/crew/counselor-troi',
    'scott': 'https://alexai-star-trek-agile.vercel.app/api/crew/chief-engineer-scott',
    'spock': 'https://alexai-star-trek-agile.vercel.app/api/crew/commander-spock',
    'worf': 'https://alexai-star-trek-agile.vercel.app/api/crew/lieutenant-worf',
    'observation-lounge': 'https://alexai-star-trek-agile.vercel.app/api/crew/observation-lounge'
  };
  return endpoints[crewMember] || endpoints['picard'];
}

return routeToCrewMember();

