parameters.jsCode:
// Ship's Computer - Mission Analysis & UI Orchestration
const userRequest = $input.first().json;
const { query, context, userRole, urgency, complexity, mission, interfacePrefs } = userRequest;

// Mission Priority Assessment
const priorityLevels = {
  'low': 1, 'medium': 2, 'high': 3, 'critical': 4
};
const urgencyLevel = urgency || 'normal';
const complexityLevel = complexity || 'medium';

// Determine mission priority
let missionPriority = 'medium';
if (urgencyLevel === 'critical' || complexityLevel === 'high') missionPriority = 'high';
if (urgencyLevel === 'low' && complexityLevel === 'low') missionPriority = 'low';

// UI Layout Configuration based on mission
const uiLayouts = {
  'critical': 'emergency-lcars',
  'high': 'tactical-lcars',
  'medium': 'standard-lcars',
  'low': 'minimal-lcars'
};

// Crew Selection Logic based on mission requirements
const crewCapabilities = {
  'strategic': ['captain-picard', 'commander-spock'],
  'technical': ['lieutenant-data', 'chief-engineer-scott'],
  'emotional': ['counselor-troi'],
  'tactical': ['lieutenant-worf'],
  'analytical': ['observation-lounge', 'lieutenant-data']
};

// Determine required crew based on context
let requiredCrew = [];
if (context.includes('strategy') || context.includes('leadership')) {
  requiredCrew.push(...crewCapabilities.strategic);
}
if (context.includes('technical') || context.includes('engineering')) {
  requiredCrew.push(...crewCapabilities.technical);
}
if (context.includes('emotional') || context.includes('counseling')) {
  requiredCrew.push(...crewCapabilities.emotional);
}
if (context.includes('tactical') || context.includes('security')) {
  requiredCrew.push(...crewCapabilities.tactical);
}
if (context.includes('analysis') || context.includes('research')) {
  requiredCrew.push(...crewCapabilities.analytical);
}

// Default crew if none specified
if (requiredCrew.length === 0) {
  requiredCrew = ['captain-picard', 'lieutenant-data'];
}

// Mission orchestration plan
const missionPlan = {
  priority: missionPriority,
  uiLayout: uiLayouts[missionPriority],
  requiredCrew: requiredCrew,
  interfaceElements: {
    missionStatus: true,
    crewHighlights: true,
    priorityIndicator: true,
    dynamicPanels: complexityLevel !== 'low'
  },
  llmStrategy: {
    primary: 'claude', // Claude for mission analysis
    secondary: 'gpt4', // GPT-4 for technical details
    emergency: 'chatgpt5' // ChatGPT 5 for critical situations
  }
};

return {
  ...userRequest,
  missionPlan,
  timestamp: new Date().toISOString(),
  shipComputer: {
    status: 'ONLINE',
    mission: mission || 'General Operations',
    crew: requiredCrew.length,
    priority: missionPriority
  }
};

// LLM Provider Selection with Role Optimization
const missionData = $input.first().json;
const { missionPlan, query, context } = missionData;

// Role-based LLM optimization
const roleLLMConfigs = {
  'captain-picard': {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.2 // Strategic thinking
  },
  'lieutenant-data': {
    provider: 'openai',
    model: 'gpt-4',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 8000,
    temperature: 0.1 // Precise analysis
  },
  'chief-engineer-scott': {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 6000,
    temperature: 0.3 // Creative solutions
  },
  'commander-spock': {
    provider: 'openai',
    model: 'gpt-4',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 6000,
    temperature: 0.1 // Logical reasoning
  },
  'lieutenant-worf': {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.2 // Tactical precision
  },
  'counselor-troi': {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.4 // Emotional intelligence
  },
  'observation-lounge': {
    provider: 'openai',
    model: 'gpt-4',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 8000,
    temperature: 0.2 // Comprehensive analysis
  }
};

// Emergency override for critical missions
if (missionPlan.priority === 'critical') {
  // Use ChatGPT 5 for critical situations
  Object.keys(roleLLMConfigs).forEach(role => {
    roleLLMConfigs[role] = {
      provider: 'openai',
      model: 'gpt-5',
      baseUrl: 'https://api.openai.com/v1',
      apiKey: '{{ $env.OPENAI_API_KEY }}',
      maxTokens: 32000, // 32k tokens for critical missions
      temperature: 0.1
    };
  });
}

// Prepare crew-specific configurations
const crewConfigs = {};
missionPlan.requiredCrew.forEach(crewMember => {
  crewConfigs[crewMember] = roleLLMConfigs[crewMember] || roleLLMConfigs['captain-picard'];
});

return {
  ...missionData,
  crewConfigs,
  llmStrategy: missionPlan.llmStrategy,
  emergencyMode: missionPlan.priority === 'critical'
};

// Multi-Crew AI Analysis with Role-Specific LLMs
const orchestrationData = $input.first().json;
const { crewConfigs, query, context, userRole } = orchestrationData;

// Process each crew member with their optimized LLM
const crewResponses = {};
const crewPromises = [];

for (const [crewMember, llmConfig] of Object.entries(crewConfigs)) {
  const crewPrompt = generateCrewPrompt(crewMember, query, context, userRole);

  // Simulate parallel LLM calls (in n8n, this would be actual HTTP requests)
  const crewResponse = {
    crewMember,
    llmConfig,
    prompt: crewPrompt,
    response: await simulateLLMCall(llmConfig, crewPrompt)
  };

  crewResponses[crewMember] = crewResponse;
}

function generateCrewPrompt(crewMember, query, context, userRole) {
  const crewPrompts = {
    'captain-picard': `As Captain Jean-Luc Picard, provide strategic leadership and mission guidance for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Strategic vision, mission objectives, and crew coordination.`,
    'lieutenant-data': `As Lieutenant Commander Data, provide technical analysis and logical reasoning for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Technical details, data analysis, and logical conclusions.`,
    'chief-engineer-scott': `As Chief Engineer Montgomery Scott, provide engineering solutions for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Technical implementation, resource optimization, and practical solutions.`,
    'commander-spock': `As Commander Spock, provide logical analysis and scientific reasoning for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Scientific method, logical deduction, and analytical insights.`,
    'lieutenant-worf': `As Lieutenant Worf, provide tactical and security analysis for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Security considerations, tactical planning, and risk assessment.`,
    'counselor-troi': `As Counselor Deanna Troi, provide emotional intelligence and human perspective for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Human factors, emotional context, and interpersonal dynamics.`,
    'observation-lounge': `As the Observation Lounge AI, provide comprehensive analysis and synthesis for: ${query}\nContext: ${context}\nUser Role: ${userRole}\nFocus on: Holistic understanding, cross-domain insights, and strategic synthesis.`
  };

  return crewPrompts[crewMember] || crewPrompts['captain-picard'];
}

async function simulateLLMCall(llmConfig, prompt) {
  // This simulates the LLM call - in actual n8n, you'd make HTTP requests
  return `Simulated response from ${llmConfig.model} for ${prompt.substring(0, 50)}...`;
}

return {
  ...orchestrationData,
  crewResponses,
  analysisComplete: true,
  timestamp: new Date().toISOString()
};

// UI Configuration Generator based on Crew Analysis
const crewAnalysis = $input.first().json;
const { crewResponses, missionPlan, shipComputer } = crewAnalysis;

// Generate dynamic UI configuration
const uiConfiguration = {
  layout: missionPlan.uiLayout,
  priority: missionPlan.priority,
  mission: shipComputer.mission,
  crew: {
    active: Object.keys(crewResponses),
    highlights: Object.keys(crewResponses).map(crew => ({
      name: crew,
      status: 'active',
      contribution: crewResponses[crew].response.substring(0, 100) + '...'
    }))
  },
  interface: {
    panels: missionPlan.interfaceElements.dynamicPanels ? 'adaptive' : 'standard',
    missionStatus: missionPlan.interfaceElements.missionStatus,
    priorityIndicator: missionPlan.interfaceElements.priorityIndicator,
    crewHighlights: missionPlan.interfaceElements.crewHighlights
  },
  lcars: {
    theme: missionPlan.priority === 'critical' ? 'emergency-red' : 'standard-orange',
    animation: missionPlan.priority === 'high' ? 'pulse' : 'standard',
    layout: missionPlan.uiLayout
  }
};

// Generate crew coordination summary
const crewSummary = Object.entries(crewResponses).map(([crew, data]) => ({
  crew: crew,
  llm: data.llmConfig.model,
  provider: data.llmConfig.provider,
  contribution: data.response.substring(0, 150) + '...',
  timestamp: new Date().toISOString()
}));

return {
  ...crewAnalysis,
  uiConfiguration,
  crewSummary,
  missionStatus: {
    status: 'COMPLETE',
    crewEngaged: crewSummary.length,
    llmProviders: [...new Set(crewSummary.map(c => c.provider))],
    priority: missionPlan.priority
  }
};

// LLM Provider Selection Logic
const userRequest = $input.first().json;
const preferredLLM = userRequest.preferredLLM || 'claude';
const useChatGPT5 = userRequest.useChatGPT5 || false;

// Determine which LLM endpoint to use
let llmConfig;

if (useChatGPT5 && preferredLLM === 'chatgpt') {
  // ChatGPT 5 Configuration
  llmConfig = {
    provider: 'openai',
    model: 'gpt-5',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.3
  };
} else if (preferredLLM === 'claude') {
  // Claude 3.5 Sonnet Configuration
  llmConfig = {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 2000,
    temperature: 0.3
  };
} else if (preferredLLM === 'gpt4') {
  // GPT-4 Configuration
  llmConfig = {
    provider: 'openai',
    model: 'gpt-4',
    baseUrl: 'https://api.openai.com/v1',
    apiKey: '{{ $env.OPENAI_API_KEY }}',
    maxTokens: 4000,
    temperature: 0.3
  };
} else {
  // Default to Claude
  llmConfig = {
    provider: 'anthropic',
    model: 'anthropic/claude-3.5-sonnet',
    baseUrl: 'https://openrouter.ai/api/v1',
    apiKey: '{{ $env.OPENROUTER_API_KEY }}',
    maxTokens: 2000,
    temperature: 0.3
  };
}

// Prepare the request with LLM configuration
const enhancedRequest = {
  ...userRequest,
  llmConfig,
  timestamp: new Date().toISOString(),
  modelInfo: {
    provider: llmConfig.provider,
    model: llmConfig.model,
    maxTokens: llmConfig.maxTokens
  }
};

return enhancedRequest;

