// Knowledge Discovery and Categorization
const processKnowledgeUpdate = () => {
  const input = $input.all()[0].json;

  // Extract information about new files or updates
  const fileType = input.fileType || 'unknown';
  const filePath = input.filePath || '';
  const content = input.content || '';
  const updateType = input.updateType || 'creation';

  // Determine knowledge domain based on file content and path
  let knowledgeDomain = '06-reference';
  let agentRelevance = [];

  // Analyze file content for categorization
  const contentLower = content.toLowerCase();
  const pathLower = filePath.toLowerCase();

  // Domain classification logic
  if (pathLower.includes('architecture') || contentLower.includes('system design') || contentLower.includes('lcars')) {
    knowledgeDomain = '01-foundations/architecture';
    agentRelevance = ['captain-picard', 'chief-engineer-scott'];
  } else if (pathLower.includes('security') || contentLower.includes('security') || contentLower.includes('authentication')) {
    knowledgeDomain = '01-foundations/standards/security-protocols';
    agentRelevance = ['lieutenant-worf', 'commander-spock'];
  } else if (pathLower.includes('agent') || pathLower.includes('crew') || pathLower.includes('n8n')) {
    knowledgeDomain = '02-ai-agents';
    agentRelevance = ['lieutenant-data', 'commander-spock'];
  } else if (pathLower.includes('deploy') || pathLower.includes('script') || contentLower.includes('deployment')) {
    knowledgeDomain = '03-operations';
    agentRelevance = ['chief-engineer-scott', 'lieutenant-data'];
  } else if (pathLower.includes('project') || contentLower.includes('agile') || contentLower.includes('kanban')) {
    knowledgeDomain = '04-projects';
    agentRelevance = ['captain-picard', 'counselor-troi'];
  } else if (pathLower.includes('learn') || contentLower.includes('evolution') || contentLower.includes('improvement')) {
    knowledgeDomain = '05-evolution';
    agentRelevance = ['commander-spock', 'lieutenant-data'];
  }

  // Extract key insights from content
  const insights = extractKeyInsights(content, fileType);

  // Generate learning opportunities
  const learningOpportunities = generateLearningOpportunities(content, agentRelevance);

  const processedUpdate = {
    ...input,
    knowledgeDomain,
    agentRelevance,
    insights,
    learningOpportunities,
    processingTimestamp: new Date().toISOString(),
    bilateral: true
  };

  return [{ json: processedUpdate }];
};

// Extract key insights from content
function extractKeyInsights(content, fileType) {
  const insights = [];

  if (fileType === 'markdown') {
    // Extract headings as key topics
    const headingMatches = content.match(/#{1,6}\s+(.+)/g);
    if (headingMatches) {
      insights.push(...headingMatches.map(h => h.replace(/#{1,6}\s+/, '')));
    }

    // Extract key phrases
    if (content.includes('‚úÖ')) insights.push('Contains completed tasks or achievements');
    if (content.includes('‚ùå')) insights.push('Contains identified issues or failures');
    if (content.includes('üéØ')) insights.push('Contains goals or targets');
    if (content.includes('‚ö†Ô∏è')) insights.push('Contains warnings or cautions');
  } else if (fileType === 'shell') {
    // Extract function names and key operations
    const functionMatches = content.match(/function\s+(\w+)|([\w_]+)\s*\(\s*\)/g);
    if (functionMatches) {
      insights.push('Contains functions: ' + functionMatches.join(', '));
    }

    if (content.includes('curl')) insights.push('Contains API interactions');
    if (content.includes('npm') || content.includes('yarn')) insights.push('Contains package management');
    if (content.includes('docker')) insights.push('Contains containerization');
  }

  return insights.slice(0, 10); // Limit to top 10 insights
}

// Generate learning opportunities for agents
function generateLearningOpportunities(content, agentRelevance) {
  const opportunities = [];

  for (const agent of agentRelevance) {
    switch (agent) {
      case 'captain-picard':
        opportunities.push({
          agent: 'captain-picard',
          opportunity: 'Strategic leadership insights and project coordination patterns',
          application: 'Apply to future strategic decision-making'
        });
        break;
      case 'lieutenant-data':
        opportunities.push({
          agent: 'lieutenant-data',
          opportunity: 'Technical implementation patterns and system operations',
          application: 'Enhance technical analysis and operational efficiency'
        });
        break;
      case 'counselor-troi':
        opportunities.push({
          agent: 'counselor-troi',
          opportunity: 'Team dynamics and collaboration effectiveness',
          application: 'Improve team coordination and user experience'
        });
        break;
      case 'chief-engineer-scott':
        opportunities.push({
          agent: 'chief-engineer-scott',
          opportunity: 'Engineering solutions and performance optimization',
          application: 'Apply to technical problem-solving and system efficiency'
        });
        break;
      case 'commander-spock':
        opportunities.push({
          agent: 'commander-spock',
          opportunity: 'Logical analysis frameworks and optimization strategies',
          application: 'Enhance analytical decision-making processes'
        });
        break;
      case 'lieutenant-worf':
        opportunities.push({
          agent: 'lieutenant-worf',
          opportunity: 'Security protocols and risk management strategies',
          application: 'Strengthen security posture and threat assessment'
        });
        break;
    }
  }

  return opportunities;
}

return processKnowledgeUpdate();

