  let crewResponse;
  try {
    if (typeof requestData === 'string') {
      crewResponse = { message: requestData };
    } else if (requestData.response) {
      crewResponse = requestData.response;
    } else {
      crewResponse = requestData;
    }
  } catch (error) {
    crewResponse = {
      message: 'I apologize, but I encountered an issue processing your request.',
      error: true
    };
  }

  const totalProcessingTime = Date.now() - requestData.processingStartTime;

  const synthesizedResponse = {
    response: crewResponse,
    metadata: {
      requestId: requestData.requestId,
      selectedCrew: requestData.selectedCrew,
      confidence: requestData.confidence,
      processingTime: totalProcessingTime,
      qualityScore: calculateQualityScore(crewResponse, requestData),
      completedAt: new Date().toISOString()
    }
  };

  return [{ json: synthesizedResponse }];
};

function calculateQualityScore(response, requestData) {
  let score = 0.5;
  const responseText = JSON.stringify(response);
  if (responseText.length > 100) score += 0.2;
  if (requestData.confidence > 0.8) score += 0.2;
  if (!response.error) score += 0.3;
  return Math.min(1.0, score);
}

return synthesizeResponse();

