// Enhanced Crew Router with Human intuition + Borg efficiency
const enhancedRouter = () => {
  const requestData = $input.all()[0].json;
  const aiResponse = requestData.choices[0].message.content;

  let selection;
  try {
    selection = JSON.parse(aiResponse);
  } catch (error) {
    // Human intuition fallback
    selection = {
      selectedCrew: 'picard',
      confidence: 0.5,
      reasoning: 'Human intuition fallback - defaulting to Captain',
      queryType: 'general'
    };
  }

  // Borg efficiency validation
  const validCrew = ['picard', 'data', 'troi', 'scott', 'spock', 'worf', 'observation-lounge'];
  if (!validCrew.includes(selection.selectedCrew)) {
    selection.selectedCrew = 'picard';
    selection.confidence = 0.3;
    selection.reasoning = 'Borg correction applied';
  }

  // Enhanced routing data (Best of Both Worlds)
  const routingData = {
    ...requestData,
    selectedCrew: selection.selectedCrew,
    confidence: selection.confidence,
    reasoning: selection.reasoning,
    queryType: selection.queryType || 'general',
    enhancementLevel: 'human-borg-hybrid',
    processingTime: Date.now(),
    crewEndpoint: getCrewEndpoint(selection.selectedCrew)
  };

  return [{ json: routingData }];
};

function getCrewEndpoint(crew) {
  const endpoints = {
    'picard': 'https://alexai-star-trek-agile.vercel.app/api/crew/captain-picard',
    'data': 'https://alexai-star-trek-agile.vercel.app/api/crew/lieutenant-data',
    'troi': 'https://alexai-star-trek-agile.vercel.app/api/crew/counselor-troi',
    'scott': 'https://alexai-star-trek-agile.vercel.app/api/crew/chief-engineer-scott',
    'spock': 'https://alexai-star-trek-agile.vercel.app/api/crew/commander-spock',
    'worf': 'https://alexai-star-trek-agile.vercel.app/api/crew/lieutenant-worf',
    'observation-lounge': 'https://alexai-star-trek-agile.vercel.app/api/crew/observation-lounge'
  };
  return endpoints[crew] || endpoints['picard'];
}

return enhancedRouter();

