// Access all input items (each one is a video)
const items = $input.all();

// For each video item, join the captions from data[]
const result = items.map(item => {
  const data = item.json.data;

  // Safely join text only if data is an array
  const fullCaption = Array.isArray(data)
    ? data.map(d => d.text).join(' ')
    : '';

  return {
    json: {
      full_caption: fullCaption
    }
  };
});

return result;

// Get all input items
const items = $input.all();

// Get current time and calculate threshold for 10 days ago
const now = new Date();
const tenDaysAgo = new Date(now.getTime() - 10 * 24 * 60 * 60 * 1000); // 10 days in ms

// Filter based on both `reply_to` and `created_at`
const filtered = items.filter(item => {
  const replyTo = item.json.reply_to;
  const createdAt = item.json.created_at;

  const isOriginalPost =
    replyTo === undefined ||
    replyTo === null ||
    replyTo === '[undefined]' ||
    (Array.isArray(replyTo) && replyTo.length === 0);

  const isRecent =
    createdAt && new Date(createdAt) >= tenDaysAgo;

  return isOriginalPost && isRecent;
});

return filtered;

const raw = $input.first().json.output || '';

const cleanedLines = raw
  .split('\n')
  .map(line => {
    const trimmed = line.trim();

    // Remove lines like "# LinkedIn content", "## undefined", or just "#"
    if (/^#+\s*(linkedin content|undefined)?$/i.test(trimmed)) return '';
    if (/^- \*\*.*\*\*:\s*#/.test(trimmed)) return '';
    if (/^#+\s*$/.test(trimmed)) return '';

    // Convert markdown headers to plain lines
    const headerMatch = trimmed.match(/^#+\s*(.+)/);
    if (headerMatch) {
      return headerMatch[1].trim();
    }

    return trimmed;
  });

// Rebuild the cleaned output while preserving paragraph breaks
let finalOutput = '';
for (let i = 0; i < cleanedLines.length; i++) {
  const line = cleanedLines[i];

  if (line === '') {
    // Preserve paragraph breaks (double line breaks)
    finalOutput += '\n\n';
  } else {
    // Regular content
    finalOutput += line + '\n';
  }
}

return [
  {
    json: {
      linkedin_post: finalOutput.trim()
    }
  }
];

