// Unified input processor for all trigger types
const inputData = $input.first().json;
let userPrompt = '';
let inputMethod = 'unknown';
let responseMode = 'json';

// Handle Form Trigger input
if (inputData.prompt && !inputData.body) {
  userPrompt = inputData.prompt;
  inputMethod = 'web_form';
  responseMode = 'form';
}
// Handle Chat Webhook input
else if (inputData.body && inputData.body.message) {
  userPrompt = inputData.body.message;
  inputMethod = 'chat_webhook';
  responseMode = 'webhook';
}
// Handle Manual Test input
else if (inputData.prompt && inputData.input_method) {
  userPrompt = inputData.prompt;
  inputMethod = inputData.input_method;
  responseMode = 'manual';
}
// Fallback
else {
  userPrompt = 'A story about human connection and unexpected transformation';
  inputMethod = 'fallback';
  responseMode = 'manual';
}

const processedData = {
  originalPrompt: userPrompt,
  inputMethod: inputMethod,
  responseMode: responseMode,
  timestamp: new Date().toISOString(),
  pipeline: {
    stage: 'initialized',
    totalSteps: 4,
    currentStep: 0
  }
};

return { json: processedData };

// Process Story Architect response
const architectResponse = $input.first().json;
const inputData = $('ğŸ”„ Input Processor').first().json;

let storyDNA;
try {
  const content = architectResponse.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  storyDNA = jsonMatch ? JSON.parse(jsonMatch[0]) : null;
} catch (e) {
  storyDNA = null;
}

// Fallback story structure if AI fails
if (!storyDNA) {
  storyDNA = {
    theme: "Human transformation through adversity",
    genre: "Drama with thriller elements",
    tone: "Contemplative building to intense",
    setting: "Contemporary urban environment",
    central_conflict: "Internal growth vs external pressures",
    protagonist_type: "Reluctant hero seeking purpose",
    antagonist_force: "Systemic opposition and self-doubt",
    story_engine: "Character's journey of self-discovery",
    target_audience: "Adult drama enthusiasts",
    visual_style: "Realistic with poetic moments",
    core_emotion: "Hope emerging from struggle",
    thematic_question: "What does it truly mean to live authentically?"
  };
}

// Add screenplay structure guidelines
storyDNA.structure = {
  totalPages: 90,
  act1: { pages: "1-30", focus: "Setup and inciting incident" },
  act2a: { pages: "31-60", focus: "Complications and character development" },
  act2b: { pages: "61-75", focus: "Crisis and transformation" },
  act3: { pages: "76-90", focus: "Climax and resolution" },
  banality_period: "Pages 1-20: Establish mundane world",
  rising_action: "Pages 21-30: Stakes emerge and escalate"
};

const enrichedData = {
  ...inputData,
  storyFoundation: storyDNA,
  pipeline: {
    stage: 'story_complete',
    totalSteps: 4,
    currentStep: 1,
    completedSteps: ['story_architect']
  }
};

return { json: enrichedData };

// Process Character Psychologist response
const characterResponse = $input.first().json;
const storyData = $('ğŸ“Š Process Story DNA').first().json;

let characterProfiles;
try {
  const content = characterResponse.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  characterProfiles = jsonMatch ? JSON.parse(jsonMatch[0]) : null;
} catch (e) {
  characterProfiles = null;
}

// Fallback characters if AI fails
if (!characterProfiles) {
  characterProfiles = {
    primary: [
      {
        name: "ALEX CHEN",
        age: "32",
        role: "Protagonist - Truth seeker",
        personality: "Intelligent, guarded, secretly longing for connection",
        motivation: "Discovering authentic purpose and meaning",
        fatal_flaw: "Fear of vulnerability and emotional intimacy",
        backstory: "Former idealist whose trust was shattered",
        arc_beginning: "Cynical and emotionally isolated",
        arc_middle: "Forced to open up and trust others",
        arc_end: "Embraces vulnerability as true strength",
        relationships: "Guided by mentor, challenged by rival",
        voice: "Sharp wit masking deeper emotional pain",
        serves_theme: "Embodies the journey from isolation to authentic connection"
      },
      {
        name: "JORDAN BLAKE",
        age: "45",
        role: "Antagonist/Opposition force",
        personality: "Charismatic but manipulative, believes ends justify means",
        motivation: "Maintaining control and existing power structures",
        fatal_flaw: "Inability to see beyond their own perspective",
        backstory: "Rose to power through compromise and calculation",
        arc_beginning: "Confident in their worldview",
        arc_middle: "Threatened by protagonist's challenge to status quo",
        arc_end: "Either transforms or faces consequences of rigidity",
        relationships: "Opposes protagonist, uses supporting characters",
        voice: "Smooth, persuasive, occasionally revealing deeper insecurity",
        serves_theme: "Represents the forces that resist authentic growth"
      }
    ],
    secondary: [
      {
        name: "SAM RIVERA",
        age: "58",
        role: "Mentor/Wise guide",
        personality: "Patient, insightful, carrying own regrets",
        motivation: "Helping others avoid their past mistakes",
        fatal_flaw: "Over-protectiveness can limit others' growth",
        backstory: "Made a crucial choice that haunts them",
        arc_beginning: "Confident teacher and advisor",
        arc_middle: "Questions whether guidance is becoming control",
        arc_end: "Learns to support without constraining",
        relationships: "Mentors protagonist, connects with other characters",
        voice: "Gentle wisdom with underlying sadness",
        serves_theme: "Shows how experience can both help and hinder growth"
      }
    ],
    tertiary: [
      {
        name: "CASEY WONG",
        age: "28",
        role: "Catalyst/Mirror character",
        personality: "Energetic but insecure, seeks external validation",
        motivation: "Proving their worth and gaining recognition",
        fatal_flaw: "Jealousy and competitive desperation",
        backstory: "Always felt overshadowed by others",
        arc_beginning: "Friendly but secretly competitive",
        arc_middle: "Competition becomes destructive obsession",
        arc_end: "Realizes that comparison steals joy",
        relationships: "Reflects protagonist's potential path",
        voice: "Eager enthusiasm hiding deep insecurity",
        serves_theme: "Shows the trap of external validation"
      }
    ]
  };
}

const enrichedStoryData = {
  ...storyData,
  characters: characterProfiles,
  pipeline: {
    stage: 'characters_complete',
    totalSteps: 4,
    currentStep: 2,
    completedSteps: ['story_architect', 'character_psychologist']
  }
};

return { json: enrichedStoryData };

// Process Structure Engineer response
const structureResponse = $input.first().json;
const characterData = $('ğŸ­ Process Characters').first().json;

let sceneStructure;
try {
  const content = structureResponse.choices[0].message.content;
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  sceneStructure = jsonMatch ? JSON.parse(jsonMatch[0]) : null;
} catch (e) {
  sceneStructure = null;
}

// Fallback structure if AI fails
if (!sceneStructure || !sceneStructure.scenes) {
  sceneStructure = {
    scenes: [
      {
        page_range: "1-8",
        act: "I",
        sequence: "Opening World",
        location: "Protagonist's everyday environment",
        time: "Morning routine",
        characters: ["Protagonist"],
        purpose: "Establish normal world and character baseline",
        emotional_beat: "Subtle dissatisfaction beneath routine",
        conflict: "Internal restlessness",
        revelation: "Character's unfulfilled potential",
        visual_elements: "Repetitive actions, symbolic imagery",
        dialogue_focus: "Reveal character through mundane interactions",
        advances_plot: "World and character establishment",
        character_development: "Show protagonist's starting emotional state"
      },
      {
        page_range: "9-20",
        act: "I",
        sequence: "Building Tension",
        location: "Various familiar settings",
        time: "Throughout the day",
        characters: ["Protagonist", "Supporting characters"],
        purpose: "Deepen character relationships and hint at coming change",
        emotional_beat: "Growing awareness of limitations",
        conflict: "Small interpersonal tensions",
        revelation: "Relationships are superficial",
        visual_elements: "Contrasts between public and private moments",
        dialogue_focus: "Subtext and unspoken feelings",
        advances_plot: "Plant seeds of central conflict",
        character_development: "Show character's isolation despite being surrounded"
      },
      {
        page_range: "21-30",
        act: "I",
        sequence: "Inciting Incident",
        location: "Place of significant change",
        time: "Pivotal moment",
        characters: ["Protagonist", "Key supporting characters"],
        purpose: "Launch main story and establish stakes",
        emotional_beat: "Shock, fear, determination",
        conflict: "External force disrupts status quo",
        revelation: "The comfortable world is an illusion",
        visual_elements: "Dramatic shift in visual language",
        dialogue_focus: "Stakes and new reality",
        advances_plot: "Point of no return",
        character_development: "Protagonist forced into action"
      }
    ]
  };
}

const completeStoryData = {
  ...characterData,
  sceneStructure: sceneStructure,
  pipeline: {
    stage: 'structure_complete',
    totalSteps: 4,
    currentStep: 3,
    completedSteps: ['story_architect', 'character_psychologist', 'structure_engineer']
  }
};

return { json: completeStoryData };

// Final screenplay integration and formatting
const screenplayResponse = $input.first().json;
const completeData = $('ğŸ¬ Process Structure').first().json;

let screenplay;
try {
  screenplay = screenplayResponse.choices[0].message.content;
} catch (e) {
  screenplay = "Error generating screenplay. Please check your OpenRouter API credentials and try again.\n\nIf you need help setting up credentials:\n1. Go to OpenRouter.ai and get an API key\n2. In n8n, create HTTP Header Auth credential\n3. Set Header Name: Authorization\n4. Set Header Value: Bearer YOUR_API_KEY";
}

// Generate title from theme or prompt
const titleSource = completeData.storyFoundation?.theme || completeData.originalPrompt;
const title = titleSource
  .split(' ')
  .slice(0, 4)
  .map(word => word.toUpperCase())
  .join(' ')
  .replace(/[^A-Z0-9\s]/g, '') || 'UNTITLED SCREENPLAY';

// Create comprehensive result
const finalResult = {
  success: true,
  title: title,
  screenplay: screenplay,
  metadata: {
    originalPrompt: completeData.originalPrompt,
    inputMethod: completeData.inputMethod,
    responseMode: completeData.responseMode,
    generatedAt: completeData.timestamp,
    storyElements: {
      theme: completeData.storyFoundation?.theme,
      genre: completeData.storyFoundation?.genre,
      tone: completeData.storyFoundation?.tone,
      setting: completeData.storyFoundation?.setting
    },
    characterCount: {
      primary: completeData.characters?.primary?.length || 0,
      secondary: completeData.characters?.secondary?.length || 0,
      tertiary: completeData.characters?.tertiary?.length || 0,
      total: (completeData.characters?.primary?.length || 0) + 
             (completeData.characters?.secondary?.length || 0) + 
             (completeData.characters?.tertiary?.length || 0)
    },
    sceneCount: completeData.sceneStructure?.scenes?.length || 0,
    estimatedRuntime: '90 minutes',
    aiPipeline: {
      storyArchitect: 'claude-3.5-sonnet - Story foundation & themes',
      characterPsychologist: 'llama-3.1-70b - Character psychology & development', 
      structureEngineer: 'gpt-4o - Scene architecture & pacing',
      dialogueMaster: 'claude-3.5-sonnet - Final screenplay & dialogue'
    },
    qualityFeatures: [
      'Multi-agent AI collaboration',
      'Specialized expertise for each story element',
      'Psychologically complex characters',
      'Professional screenplay formatting',
      'Industry-standard structure',
      'Thematic integration throughout'
    ]
  }
};

return { json: finalResult };

