// Process crew request and determine appropriate crew member
const request = $input.first().json;
const { query, context, userRole, urgency, complexity, mission, interfacePrefs } = request;

// Crew member selection logic
let selectedCrew = 'captain-picard'; // default
let reasoning = 'Default selection for general requests';

if (context === 'technical' || context === 'engineering') {
  selectedCrew = 'lieutenant-data';
  reasoning = 'Technical context requires Data\'s analytical capabilities';
} else if (context === 'security' || context === 'tactical') {
  selectedCrew = 'lieutenant-worf';
  reasoning = 'Security context requires Worf\'s tactical expertise';
} else if (context === 'counseling' || context === 'diplomatic') {
  selectedCrew = 'counselor-troi';
  reasoning = 'Counseling context requires Troi\'s empathic abilities';
} else if (context === 'engineering' || context === 'systems') {
  selectedCrew = 'chief-engineer-scott';
  reasoning = 'Engineering context requires Scotty\'s technical knowledge';
} else if (context === 'strategic' || context === 'command') {
  selectedCrew = 'captain-picard';
  reasoning = 'Strategic context requires Picard\'s leadership';
} else if (context === 'crew-meeting' || context === 'coordination') {
  selectedCrew = 'observation-lounge';
  reasoning = 'Crew coordination requires observation lounge meeting';
}

// Prepare crew response
const crewResponse = {
  selectedCrew,
  reasoning,
  request: {
    query,
    context,
    userRole,
    urgency,
    complexity,
    mission,
    interfacePrefs
  },
  timestamp: new Date().toISOString(),
  webhookId: 'crew-request'
};

return crewResponse;
