parameters.responseBody:
{
  "timestamp": "{{ $now }}",
  "businessMetrics": {
    "revenue": {{ $json.revenue || 0 }},
    "expenses": {{ $json.expenses || 0 }},
    "customers": {{ $json.customers || 0 }},
    "growthRate": {{ $json.growthRate || 0 }},
    "marketShare": {{ $json.marketShare || 0 }},
    "employeeCount": {{ $json.employeeCount || 0 }},
    "projectCount": {{ $json.projectCount || 0 }},
    "completionRate": {{ $json.completionRate || 0 }}
  },
  "keyPerformanceIndicators": {
    "profitMargin": {{ $json.revenue > 0 ? (($json.revenue - $json.expenses) / $json.revenue * 100) | round(2) : 0 }},
    "customerValue": {{ $json.customers > 0 ? ($json.revenue / $json.customers) | round(2) : 0 }},
    "efficiencyRatio": {{ $json.employeeCount > 0 ? ($json.revenue / $json.employeeCount) | round(2) : 0 }},
    "projectSuccess": {{ $json.projectCount > 0 ? ($json.completionRate / $json.projectCount * 100) | round(2) : 0 }}
  },
  "insights": [
    {{ $json.revenue > 0 && (($json.revenue - $json.expenses) / $json.revenue * 100) > 20 ? "\"Excellent profit margins indicate strong financial health\"" : $json.revenue > 0 && (($json.revenue - $json.expenses) / $json.revenue * 100) > 10 ? "\"Good profit margins with room for optimization\"" : "\"Profit margins need attention - consider cost reduction strategies\"" }},
    {{ $json.growthRate > 15 ? "\"Strong growth trajectory - maintain momentum\"" : $json.growthRate > 5 ? "\"Steady growth - look for acceleration opportunities\"" : "\"Growth needs attention - review business strategy\"" }},
    {{ $json.customers > 0 && ($json.revenue / $json.customers) > 1000 ? "\"High customer value - excellent customer acquisition strategy\"" : $json.customers > 0 && ($json.revenue / $json.customers) > 500 ? "\"Good customer value - consider upselling opportunities\"" : "\"Customer value optimization needed - review pricing strategy\"" }}
  ],
  "recommendations": [
    {{ $json.revenue > 0 && (($json.revenue - $json.expenses) / $json.revenue * 100) < 15 ? "\"Implement cost optimization strategies\", \"Review pricing models for margin improvement\"" : "\"Maintain current financial performance\"" }},
    {{ $json.growthRate < 10 ? "\"Explore new market opportunities\", \"Enhance marketing and sales strategies\"" : "\"Continue growth momentum\"" }},
    {{ $json.employeeCount > 0 && ($json.revenue / $json.employeeCount) < 50000 ? "\"Optimize operational efficiency\", \"Consider automation opportunities\"" : "\"Maintain operational excellence\"" }}
  ],
  "riskAssessment": {
    "financialRisk": "{{ $json.revenue > 0 && (($json.revenue - $json.expenses) / $json.revenue * 100) < 10 ? 'High' : $json.revenue > 0 && (($json.revenue - $json.expenses) / $json.revenue * 100) < 20 ? 'Medium' : 'Low' }}",
    "growthRisk": "{{ $json.growthRate < 5 ? 'High' : $json.growthRate < 15 ? 'Medium' : 'Low' }}",
    "operationalRisk": "{{ $json.employeeCount > 0 && ($json.revenue / $json.employeeCount) < 30000 ? 'High' : $json.employeeCount > 0 && ($json.revenue / $json.employeeCount) < 60000 ? 'Medium' : 'Low' }}"
  },
  "marketPosition": {
    "competitiveAdvantage": "{{ $json.marketShare > 20 ? 'Strong' : $json.marketShare > 10 ? 'Moderate' : 'Developing' }}",
    "marketOpportunity": "{{ $json.growthRate > 15 ? 'High' : $json.growthRate > 5 ? 'Medium' : 'Low' }}"
  },
  "nextSteps": [
    "Monitor KPIs weekly for trend analysis",
    "Implement top 3 recommendations within 30 days",
    "Schedule monthly business intelligence review",
    "Track progress against industry benchmarks"
  ]
}
