// Agent Enhancement Notification
const notifyAgents = () => {
  const input = $input.all()[0].json;

  // Create notifications for relevant agents
  const notifications = [];

  for (const agent of input.agentRelevance) {
    notifications.push({
      agent: agent,
      notificationType: 'knowledge-enhancement',
      message: `New knowledge available in domain: ${input.knowledgeDomain}`,
      insights: input.insights,
      learningOpportunities: input.learningOpportunities.filter(o => o.agent === agent),
      timestamp: new Date().toISOString(),
      priority: input.updateType === 'critical' ? 'high' : 'normal'
    });
  }

  // Create summary for system monitoring
  const summary = {
    totalAgentsNotified: notifications.length,
    knowledgeDomain: input.knowledgeDomain,
    insightsGenerated: input.insights.length,
    learningOpportunitiesCreated: input.learningOpportunities.length,
    bilateralLearningActive: true,
    processingComplete: true,
    timestamp: new Date().toISOString()
  };

  return [
    { json: { notifications, summary } }
  ];
};

return notifyAgents();
